/* css reset */
*{margin: 0; padding: 0; box-sizing: border-box;}
*::before,*::after{margin: 0; padding: 0; box-sizing: border-box;}
ul,ol{list-style: none;}
a{text-decoration: none; color: initial;}
img{max-width: 100%; max-height: 100%;}

/* */
.icon {
    width: 1em; height: 1em;
    vertical-align: -0.15em;
    fill: currentColor;
    overflow: hidden;
}

body {
    background: url(c3b6550972af25304f88583a51acf041.png)
}

.globalHeader{
    margin: 20px;
}

@media (min-width: 500px) {
    .globalHeader {
      margin: 60px 0 100px;
    }
  }

.searchForm{
    display: flex;
    margin-bottom: 20px;
}

@media (min-width: 500px) {
    .searchForm {
      max-width: 400px;
      margin-left: auto;
      margin-right: auto;
    }
}
.searchForm > input{
    width: 100%;
    height: 40px;
    padding: 0 10px;
    border: 1px solid #eee;
    border-radius: 4px;
}


.searchForm > button{
    white-space: nowrap;
    margin-left: 10px;
    padding: 0 24px;
    font-size: 16px;
    background: #24ADE4;
    color: #fff;
    border: none;
    border-radius: 4px;
}

.globalMain{
    max-width: 900px;
    margin-left: auto;
    margin-right: auto;
}

.siteList{
    margin: 20px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
}

@media (min-width: 500px){
    .siteList{
      margin-left: 0;
      margin-right: -25px;
      justify-content: flex-start;
    }
  }

.siteList > li {
    width: 160px;
    margin-bottom: 10px;
}

@media(min-width: 500px){
    .siteList > li {
      margin-right: 25px;
    }
}

.siteList .site{
    padding: 13px 0;
    background: #fff;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    cursor: pointer;
    position: relative;
}
.siteList .site .logo{
    font-size: 64px;
    height: 75px;
}
.siteList .site .link{
    font-size: 14px;
}

.siteList .site .close{
    position: absolute;
    top: 10%;
    right: 5%;
    display: none;
}

.siteList .site .close > .icon{
    width: 20px;
    height: 20px;
}

.siteList .site:hover .close{
    display: block;
    cursor: initial;
}

.siteList .addButton .icon-wrapper{
    background: #fff;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 13px 0;
}

.siteList .addButton .icon-wrapper .icon{
    width: 75px;
    height: 75px;
}

.siteList .addButton .icon-wrapper .text{
    font-size: 14px;
}
